#if defined(HAVE_HASH_MAP)
#include <hash_map>
#elif defined(HAVE_EXT_HASH_MAP)
#include <ext/hash_map>
#elif defined(HAVE_HASH_MAP_H)
#include <hash_map.h>
#else
#error "std::hash_map is not available with this compiler"
#endif

programlistingco.programlisting:
module VPR
{
   external;
   modules/vapor;
}

module Tweek
{
   depend VPR;
   modules/tweek;
}

module VPR
{
   external;
   modules/vapor: VPR_CONFIG=vpr-config, VPR_BASE_DIR=instlinks;
}

module Tweek
{
   depend VPR;
   modules/tweek: TWEEK_CONFIG=tweek-config, TWEEK_BASE_DIR=instlinks;
}

listitem.para:
The targets used to compile the final binaries
                           for the module's libraries, including static,
                           dynamic, and profiled versions.
