package org.vrjuggler.tweek.wizard;

import javax.swing.JComponent;

/**
 * The root wizard sequence is the root step for the wizard tree. Wizard steps
 * in this sequence are essentially part of the trunk of the wizard sequence.
 */
public class RootWizardSequence
   extends WizardSequence
{
   /**
    * You always want to enter the root sequence. ALWAYS returns true.
    */
   protected boolean shouldEnter()
   {
      return true;
   }
}
