         );
      mThread = new vpr::Thread(mFunctor);
   }

private:
   void run(void* args)
   {
      // Do some work ...
   }

   vpr::ThreadMemberFunctor<MyClass_VPR10>* mFunctor;
   vpr::Thread* mThread;
};

