using namespace gmtl;

void MPApp::draw()
{
   glClear(GL_DEPTH_BUFFER_BIT);

   // --- Setup for drawing --- //
   glMatrixMode(GL_MODELVIEW);

   glColor3f(0.0f, 0.5f, 0.75f);
   glPushMatrix();
      glScalef(15.0f,15.0f,15.0f);
      glRotatef(-90.0f,1.0,0.0,0.0);
      mMesh.render();                // Render the mesh
   glPopMatrix();
}

