#include <vpr/Thread/Thread.h>

class MyClass
{
public:
   MyClass()
      : mThread(NULL)
      , mDoWork(true)
   {
   }

   ~MyClass()
   {
      // Stop threadFunc().
      mDoWork = false;

      // Wait for mThread to complete and delete it.
      if ( NULL != mThread )
      {
         mThread->join();
         delete mThread;
         mThread = NULL;
      }
   }

   void startThread()
   {
      mThread = new vpr::Thread(boost::bind(&MyClass::threadFunc,
                                            this));
   }

private:
   void threadFunc()
   {
      while ( mDoWork )
      {
         // Do work ...
      }
   }

   bool mDoWork;
   vpr::Thread* mThread;
};

#include <vpr/Thread/Thread.h>

class Callable
{
public:
   Callable()
      : mDoWork(true)
   {
   }

   void stop()
   {
      mDoWork = false;
   }

   void operator()()
   {
      while ( mDoWork )
      {
         // Do work ...
      }
   }

private:
   bool mDoWork;
};

class MyClass
{
public:
   MyClass()
      : mThread(NULL)
   {
   }

   ~MyClass()
   {
      // Stop the functor().
      stopThread();

      // Wait for mThread to complete and delete it.
      if ( NULL != mThread )
      {
         mThread->join();
         delete mThread;
         mThread = NULL;
      }
   }

   void startThread()
   {
      mThread = new vpr::Thread(mFunctor);
   }

   void stopThread()
   {
      mFunctor.stop();

      if ( NULL != mThread )
      {
         mThread->join();
      }
   }

private:
   Callable     mFunctor;
   vpr::Thread* mThread;
};

try
{
   // Nothing will be assigned to mThread if an exception is
   // thrown from the constructor.
   mThread = new vpr::Thread(/* Args here... */);
}
// No thread functor was set.
catch (vpr::IllegalArgumentException& ex)
{
   // Handling code...
}
// The platform-specific thread object could not be allocated.
catch (vpr::ResourceException& ex)
{
   // Handling code...
}
// Something else went wrong during thread allocation.
catch (vpr::Exception& ex)
{
   std::cerr << ex.what() << std::endl;
}

